import TopBar from "../../components/topbar/TopBar";
import SideBar from "../../components/sidebar/SideBar";
import RightBar from "../../components/rightbar/RightBar";
import Feed from "../../components/feed/Feed";
import "./home.css";
import { useRef } from "react";

export const HomePage = () => {
  const sidebarRef = useRef(null);
  const mainContentRef = useRef(null);

  // Function to handle scroll events on the main content
  const handleMainContentScroll = () => {
    if (sidebarRef.current && mainContentRef.current) {
      // Sync the scroll position of the sidebar with the main content
      sidebarRef.current.scrollTop = mainContentRef.current.scrollTop;
    }
  };
  return (
    <>
      <TopBar />
      <div
        ref={mainContentRef}
        onScroll={handleMainContentScroll}
        className="homeContainer"
      >
        <SideBar />
        <Feed />
        <RightBar ref={sidebarRef} />
      </div>
    </>
  );
};
